import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function JobResults() {
    const { state } = useLocation();
    const navigate = useNavigate();
    const results = state?.data || [];

    const handleApply = async (link) => {
        try {
            // Navigate to status page first, then trigger application
            navigate('/status', { state: { status: 'Applying...', link } });

            // Send application request
            const res = await axios.post('http://localhost:5000/apply', { link });

            // Navigate again with result
            navigate('/status', {
                state: {
                    status: res.data.message || 'Application Submitted!',
                    link
                }
            });
        } catch (err) {
            navigate('/status', {
                state: {
                    status: err.response?.data?.error || 'Application Failed!',
                    link
                }
            });
        }
    };

    return (
        <div className="container">
            <h2 className="text-center">Recommended Job Roles</h2>
            <div className="results-container">
                {results.map((role, index) => (
                    <div key={index} className="role-card">
                        <h3>{role.role}</h3>
                        <div>
                            <h4>LinkedIn Jobs:</h4>
                            {role.linkedin_links.map((link, i) => (
                                <div key={i} style={{ display: 'flex', gap: '10px', marginBottom: '8px' }}>
                                    <button onClick={() => handleApply(link)}>Apply</button>
                                    <a href={link} target="_blank" rel="noopener noreferrer">{link}</a>
                                </div>
                            ))}
                        </div>
                        <div>
                            <h4>Workday Jobs:</h4>
                            {role.workday_links.map((link, i) => (
                                <div key={i} style={{ display: 'flex', gap: '10px', marginBottom: '8px' }}>
                                    <button onClick={() => handleApply(link)}>Apply</button>
                                    <a href={link} target="_blank" rel="noopener noreferrer">{link}</a>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
